import React, { useState } from "react";
import DeleteIcon from '@mui/icons-material/Delete';
import Modal from 'react-modal';
import './Style.css';
import Alert from "../Alert/Alert";

export default function DeletarProduto({ produto, onDelete }) {
    const [modalOpen, setModalOpen] = useState(false);
    const [showAlert, setShowAlert] = useState(false);

    const abrirModal = () => {
      setModalOpen(true);
    };

    const fecharModal = () => {
      setModalOpen(false);
    };

    const executarDelecaoProduto = async () => {
      try {
        await onDelete(produto.id);
        setShowAlert(true);
        abrirModal();
      } catch (error) {
        console.error("Erro ao deletar produto", error);
      }
    };

    return (
      <>
        <button className='btn-deletar-produto' onClick={abrirModal}>
          <DeleteIcon />
        </button>

        <Modal
          isOpen={modalOpen}
          onRequestClose={fecharModal}
          className="modal"
          style={{
            overlay: {
              backgroundColor: 'var(--azul-escuro)',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              textAlign: 'center',
              marginTop: '10%',
              color: 'var(--cor-da-fonte)',
            },
          }}
        >
          <h2>Você tem certeza que deseja excluir o produto?</h2>
          <div className="modal-buttons">
            <button onClick={executarDelecaoProduto}>Sim</button>
            <button onClick={fecharModal}>Não</button>
          </div>
        </Modal>

        {showAlert && (
          <Alert type="success" message="Produto excluído com sucesso!" />
        )}
      </>
    );
  }
